(window.webpackJsonp=window.webpackJsonp||[]).push([[19],{"0cbc6bead9fc379ed4fa":function(t,e,p){"use strict";p.r(e),e.default=p.p+"658710132965e7e3dc81bc7a868506e7.png"},cdbbe3ed9a225833fd61:function(t,e,p){var a="<h2 id=\u56fd\u9645\u5316>\u56fd\u9645\u5316</h2> <p>\u6bcf\u5929\u6709\u4e0a\u4ebf\u7684\u4eba\u7528\u6570\u767e\u79cd\u8bed\u8a00\u5199\u7740\u5404\u79cd\u6587\u6863\u3002 \u4e3a\u4e86\u771f\u6b63\u5b9e\u73b0\u4e07\u7ef4\u7f51\u7684\u76ee\u6807\uff0cHTTP \u8981\u80fd\u591f\u4f20\u8f93\u548c\u5904\u7406\u7528\u591a\u79cd\u8bed\u8a00\u548c\u5b57\u6bcd\u8868\u7f16\u5199\u7684\u56fd\u9645\u6027\u6587\u6863\u3002</p> <h3 id=http\u5bf9\u56fd\u9645\u6027\u5185\u5bb9\u7684\u652f\u6301>HTTP\u5bf9\u56fd\u9645\u6027\u5185\u5bb9\u7684\u652f\u6301</h3> <p>\u670d\u52a1\u5668\u901a\u8fc7 HTTP \u534f\u8bae\u7684 Content-Type \u9996\u90e8\u4e2d\u7684 charset \u53c2\u6570\u548c Content- Language \u9996\u90e8\u544a\u77e5\u5ba2\u6237\u7aef\u6587\u6863\u7684\u5b57\u6bcd\u8868\u548c\u8bed\u8a00\u3002</p> <p>\u5ba2\u6237\u7aef\u53d1\u9001 Accept-Charset \u9996\u90e8\u548c Accept-Language \u9996\u90e8\uff0c\u544a\u77e5\u670d\u52a1\u5668 \u5b83\u7406\u89e3\u54ea\u4e9b\u5b57\u7b26\u96c6\u7f16\u7801\u7b97\u6cd5\u548c\u8bed\u8a00\u4ee5\u53ca\u5176\u4e2d\u7684\u4f18\u5148\u987a\u5e8f\u3002</p> <p>\u4e0b\u9762\u7684 HTTP \u62a5\u6587\u4e2d\u7684\u8fd9\u4e9b Accept \u9996\u90e8\u53ef\u80fd\u662f\u6bcd\u8bed\u4e3a\u6cd5\u8bed\u7684\u4eba\u53d1\u51fa\u7684\u3002\u4ed6\u559c\u6b22 \u4f7f\u7528\u6bcd\u8bed\uff0c\u4f46\u4e5f\u4f1a\u8bf4\u4e00\u70b9\u513f\u82f1\u8bed\uff0c\u4ed6\u7684\u6d4f\u89c8\u5668\u652f\u6301 iso-8859-1 \u897f\u6b27\u5b57\u7b26\u96c6\u7f16\u7801\u548c UTF-8 Unicode \u5b57\u7b26\u96c6\u7f16\u7801:</p> <pre class=hljs><code class=language-http><span class=hljs-attribute>Accept-Language</span>: fr, en;q=0.8 \n<span class=hljs-attribute>Accept-Charset</span>: iso-8859-1, utf-8</code></pre><p>\u53c2\u6570\u201cq=0.8\u201d\u662f\u8d28\u91cf\u56e0\u5b50(quality factor)\uff0c\u8bf4\u660e\u82f1\u8bed\u7684\u4f18\u5148\u7ea7(0.8)\u6bd4\u6cd5\u8bed\u4f4e (\u9ed8\u8ba4\u503c\u662f 1.0)\u3002</p> <h3 id=\u5b57\u7b26\u96c6\u4e0ehttp>\u5b57\u7b26\u96c6\u4e0eHTTP</h3> <h4 id=\u5b57\u7b26\u96c6\u662f\u628a\u5b57\u7b26\u8f6c\u6362\u4e3a\u4e8c\u8fdb\u5236\u7801\u7684\u7f16\u7801>\u5b57\u7b26\u96c6\u662f\u628a\u5b57\u7b26\u8f6c\u6362\u4e3a\u4e8c\u8fdb\u5236\u7801\u7684\u7f16\u7801</h4> <p>HTTP \u5b57\u7b26\u96c6\u7684\u503c\u8bf4\u660e\u5982\u4f55\u628a\u5b9e\u4f53\u5185\u5bb9\u7684\u4e8c\u8fdb\u5236\u7801\u8f6c\u6362\u4e3a\u7279\u5b9a\u5b57\u6bcd\u8868\u4e2d\u7684\u5b57\u7b26\u3002 \u6bcf\u4e2a\u5b57\u7b26\u96c6\u6807\u8bb0\u90fd\u547d\u540d\u4e86\u4e00\u79cd\u628a\u4e8c\u8fdb\u5236\u7801\u8f6c\u6362\u4e3a\u5b57\u7b26\u7684\u7b97\u6cd5(\u53cd\u4e4b\u4ea6\u7136)\u3002 \u5b57\u7b26\u96c6\u6807\u8bb0\u5728\u7531 IANA \u7ef4\u62a4(<a href=http://www.iana.org/assignments/character-sets>\u53c2\u89c1</a>)\u7684 MIME \u5b57 \u7b26\u96c6\u6ce8\u518c\u673a\u6784\u8fdb\u884c\u4e86\u6807\u51c6\u5316\u3002</p> <h4 id=\u5b57\u7b26\u96c6\u548c\u7f16\u7801\u5982\u4f55\u5de5\u4f5c>\u5b57\u7b26\u96c6\u548c\u7f16\u7801\u5982\u4f55\u5de5\u4f5c</h4> <p>\u628a\u4e8c\u8fdb\u5236\u7801\u8f6c\u6362\u4e3a\u5b57\u7b26\u8981\u7ecf\u8fc7\u4e24\u4e2a\u6b65\u9aa4</p> <ul> <li>\u6587\u6863\u4e2d\u7684\u4e8c\u8fdb\u5236\u7801\u88ab\u8f6c\u6362\u6210\u5b57\u7b26\u4ee3\u7801\uff0c\u5b83\u8868\u793a\u4e86\u7279\u5b9a\u7f16\u7801\u5b57\u7b26\u96c6\u4e2d\u67d0\u4e2a\u7279\u5b9a\u7f16\u53f7\u7684\u5b57\u7b26\u3002</li> <li>\u5b57\u7b26\u4ee3\u7801\u7528\u4e8e\u4ece\u7f16\u7801\u7684\u5b57\u7b26\u96c6\u4e2d\u9009\u62e9\u7279\u5b9a\u7684\u5143\u7d20\u3002</li> </ul> <p><img src="+p("302492d1afd5e0e000f5")(p("0cbc6bead9fc379ed4fa"))+' alt=""></p> <h4 id=\u6807\u51c6\u5316\u7684mime-charset\u503c>\u6807\u51c6\u5316\u7684MIME charset\u503c</h4> <p>\u7279\u5b9a\u7684\u5b57\u7b26\u7f16\u7801\u65b9\u6848\u548c\u7279\u5b9a\u7684\u5df2\u7f16\u7801\u5b57\u7b26\u96c6\u7ec4\u5408\u6210\u4e00\u4e2a MIME \u5b57\u7b26\u96c6(MIME charset)\u3002 HTTP \u5728 Content-Type \u548c Accept-Charset \u9996\u90e8\u4e2d\u4f7f\u7528\u6807\u51c6\u5316\u7684 MIME charset \u6807\u8bb0\u3002 MIME charset \u7684\u503c\u90fd\u4f1a\u5728 IANA \u6ce8\u518c\u3002</p> <h4 id=content-type\u9996\u90e8\u548ccharset\u9996\u90e8\u4ee5\u53cameta\u6807\u5fd7>Content-Type\u9996\u90e8\u548cCharset\u9996\u90e8\u4ee5\u53caMETA\u6807\u5fd7</h4> <p>Web \u670d\u52a1\u5668\u901a\u8fc7\u5728 Content-Type \u9996\u90e8\u4e2d\u4f7f\u7528 charset \u53c2\u6570\u628a MIME \u5b57\u7b26\u96c6\u6807\u8bb0 \u53d1\u9001\u7ed9\u5ba2\u6237\u7aef:</p> <pre class=hljs><code class=language-http><span class=hljs-attribute>Content-Type</span>: text/html; charset=gb2312</code></pre><p>\u5bf9\u4e8e HTML \u5185\u5bb9\u6765\u8bf4\uff0c \u53ef\u4ee5\u5728\u63cf\u8ff0 charset \u7684 </p> <pre class=hljs><code class=language-html><span class=hljs-tag>&lt;<span class=hljs-name>META</span> <span class=hljs-attr>HTTP-EQUIV</span>=<span class=hljs-string>"Content-Type"</span>&gt;</span> </code></pre><p>\u6807\u8bb0\u4e2d\u627e\u5230\u5b57\u7b26\u96c6\u3002</p> <h4 id=accept-charset\u9996\u90e8>Accept-Charset\u9996\u90e8</h4> <p>HTTP \u5ba2\u6237\u7aef\u53ef\u4ee5\u4f7f\u7528 Accept-Charset \u8bf7\u6c42\u9996\u90e8\u6765\u660e\u786e\u544a\u77e5\u670d\u52a1\u5668\u5b83\u652f\u6301\u54ea\u4e9b\u5b57 \u7b26\u7cfb\u7edf\u3002 Accept-Charset \u9996\u90e8\u7684\u503c\u5217\u51fa\u4e86\u5ba2\u6237\u7aef\u652f\u6301\u7684\u5b57\u7b26\u7f16\u7801\u65b9\u6848\u3002</p> <pre class=hljs><code class=language-http><span class=hljs-attribute>Accept-Charset</span>: iso-8859-1, utf-8</code></pre><h3 id=\u591a\u8bed\u8a00\u5b57\u7b26\u7f16\u7801\u5165\u95e8>\u591a\u8bed\u8a00\u5b57\u7b26\u7f16\u7801\u5165\u95e8</h3> <p>\u5bf9\u4e8e\u5de5\u4f5c\u4e2d\u8981\u5f00\u53d1\u5927\u91cf\u56fd\u9645\u5316\u5e94\u7528\u7684 HTTP \u7a0b\u5e8f\u5458\u6765\u8bf4\uff0c\u4e3a\u4e86\u80fd\u7406\u89e3\u6280\u672f\u89c4\u8303\u548c\u76f8\u5e94\u7684\u5b9e\u73b0\u8f6f\u4ef6\uff0c\u9700\u8981\u6df1\u5165\u5730\u7406\u89e3\u591a\u8bed\u8a00\u5b57\u7b26\u7cfb\u7edf\u3002</p> <h4 id=terminology>Terminology</h4> <p>\u7535\u5b50\u5316\u5b57\u7b26\u7cfb\u7edf\u7684 8 \u4e2a\u672f\u8bed</p> <ul> <li>\u5b57\u7b26</li> </ul> <p>\u5b57\u7b26\u662f\u6307\u5b57\u6bcd\u3001\u6570\u5b57\u3001\u6807\u70b9\u3001\u8868\u610f\u6587\u5b57(\u6bd4\u5982\u6c49\u8bed)\u3001\u7b26\u53f7\uff0c\u6216\u5176\u4ed6\u6587\u672c\u5f62\u5f0f\u7684\u4e66\u5199\u201c\u539f\u5b50\u201d\u3002 \u7531\u7edf\u4e00\u5b57\u7b26\u96c6(Universal Character Set\uff0cUCS, \u5b83\u7684\u975e\u6b63\u5f0f\u7684\u540d\u5b57\u662f Unicode3)\u9996\u521b\uff0c \u4e3a\u591a\u79cd\u8bed\u8a00\u4e2d\u7684\u5f88\u591a\u5b57\u7b26\u5f00\u53d1\u4e86\u4e00\u7cfb\u5217\u6807\u51c6\u5316\u7684\u6587\u672c\u540d\u79f0\uff0c \u5b83\u4eec\u5e38\u7528\u6765\u4fbf\u6377\u5730\u547d\u540d\u5b57\u7b26\uff0c\u800c\u4e14\u4e0d\u4f1a\u4e0e\u5176\u4ed6\u5b57\u7b26\u51b2\u7a81\u3002</p> <ul> <li>\u5b57\u5f62 </li> </ul> <p>\u63cf\u8ff0\u5b57\u7b26\u7684\u7b14\u753b\u56fe\u6848\u6216\u552f\u4e00\u7684\u56fe\u5f62\u5316\u5f62\u72b6\u3002\u5982\u679c\u4e00\u4e2a\u5b57\u7b26\u6709\u591a\u79cd\u4e0d\u540c\u7684\u5199\u6cd5\uff0c\u5c31 \u6709\u591a\u4e2a\u5b57\u5f62(\u53c2\u89c1\u56fe 16-3)\u3002</p> <ul> <li>\u7f16\u7801\u540e\u7684\u5b57\u7b26 </li> </ul> <p>\u5206\u914d\u7ed9\u5b57\u7b26\u7684\u552f\u4e00\u6570\u5b57\u7f16\u53f7\uff0c\u8fd9\u6837\u6211\u4eec\u5c31\u53ef\u4ee5\u64cd\u4f5c\u5b83\u4e86\u3002</p> <ul> <li>\u4ee3\u7801\u7a7a\u95f4 </li> </ul> <p>\u8ba1\u5212\u7528\u4e8e\u5b57\u7b26\u4ee3\u7801\u503c\u7684\u6574\u6570\u8303\u56f4\u3002</p> <ul> <li>\u4ee3\u7801\u5bbd\u5ea6 </li> </ul> <p>\u6bcf\u4e2a(\u56fa\u5b9a\u5927\u5c0f\u7684)\u5b57\u7b26\u4ee3\u7801\u6240\u7528\u7684\u4f4d\u6570\u3002</p> <ul> <li>\u5b57\u7b26\u5e93 </li> </ul> <p>\u7279\u5b9a\u7684\u5de5\u4f5c\u5b57\u7b26\u96c6(\u5168\u4f53\u5b57\u7b26\u7684\u4e00\u4e2a\u5b50\u96c6)\u3002</p> <ul> <li>\u7f16\u7801\u540e\u7684\u5b57\u7b26\u96c6 </li> </ul> <p>\u7ec4\u6210\u5b57\u7b26\u5e93(\u4ece\u5168\u7403\u7684\u5b57\u7b26\u4e2d\u9009\u51fa\u82e5\u5e72\u5b57\u7b26)\u7684\u5df2\u7f16\u7801\u5b57\u7b26\u96c6\uff0c\u5e76\u4e3a\u6bcf\u4e2a\u5b57\u7b26 \u5206\u914d\u4ee3\u7801\u7a7a\u95f4\u4e2d\u7684\u4e00\u4e2a\u4ee3\u7801\u3002\u6362\u53e5\u8bdd\u8bf4\uff0c\u5b83\u628a\u6570\u5b57\u5316\u7684\u5b57\u7b26\u4ee3\u7801\u6620\u5c04\u4e3a\u5b9e\u9645\u7684 \u5b57\u7b26\u3002</p> <ul> <li>\u5b57\u7b26\u7f16\u7801\u65b9\u6848 </li> </ul> <p>\u628a\u6570\u5b57\u5316\u7684\u5b57\u7b26\u4ee3\u7801\u7f16\u7801\u6210\u4e00\u7cfb\u5217\u4e8c\u8fdb\u5236\u7801(\u5e76\u80fd\u76f8\u5e94\u5730\u53cd\u5411\u89e3\u7801)\u7684\u7b97\u6cd5\u3002\u5b57 \u7b26\u7f16\u7801\u65b9\u6848\u53ef\u7528\u6765\u51cf\u5c11\u8bc6\u522b\u5b57\u7b26\u6240\u9700\u8981\u7684\u6570\u636e\u603b\u91cf(\u538b\u7f29)\u3001\u89e3\u51b3\u4f20\u8f93\u9650\u5236\u3001\u7edf \u4e00\u91cd\u53e0\u7f16\u7801\u5b57\u7b26\u96c6\u3002</p> <h3 id=\u8bed\u8a00\u6807\u8bb0\u4e0ehttp>\u8bed\u8a00\u6807\u8bb0\u4e0eHttp</h3> <p>\u8bed\u8a00\u6807\u8bb0\u662f\u547d\u540d\u53e3\u8bed\u7684\u6807\u51c6\u5316\u5b57\u7b26\u4e32\u77ed\u8bed\u3002</p> <h4 id=content-language\u9996\u90e8>Content-Language\u9996\u90e8</h4> <p>\u5b9e\u4f53\u7684 Content-Language \u9996\u90e8\u5b57\u6bb5\u63cf\u8ff0\u5b9e\u4f53\u7684\u76ee\u6807\u53d7\u4f17\u8bed\u8a00\u3002</p> <pre class=hljs><code class=language-http><span class=hljs-attribute>Content-Language</span>: zh-CN</code></pre><p>Content-Language \u9996\u90e8\u4e0d\u4ec5\u9650\u4e8e\u6587\u672c\u6587\u6863\u3002 \u97f3\u9891\u7247\u6bb5\u3001\u7535\u5f71\u4ee5\u53ca\u5e94\u7528\u7a0b\u5e8f\u90fd\u6709\u53ef \u80fd\u662f\u9762\u5411\u7279\u5b9a\u8bed\u8a00\u53d7\u4f17\u7684\u3002 \u4efb\u4f55\u9762\u5411\u7279\u5b9a\u8bed\u8a00\u53d7\u4f17\u7684\u5a92\u4f53\u7c7b\u578b\u90fd\u53ef\u4ee5\u6709 Content- Language \u9996\u90e8\u3002</p> <p>\u5982\u679c\u5185\u5bb9\u662f\u9762\u5411\u591a\u79cd\u8bed\u8a00\u53d7\u4f17\u7684\uff0c\u53ef\u4ee5\u5217\u51fa\u591a\u79cd\u8bed\u8a00\u3002</p> <pre class=hljs><code class=language-http><span class=hljs-attribute>Content-Language</span>: zh-CN, zh-TW, zh-HK</code></pre><h4 id=accept-language\u9996\u90e8>Accept-Language\u9996\u90e8</h4> <p>HTTP \u5141\u8bb8\u6211\u4eec\u628a\u8bed\u8a00\u65b9\u9762\u7684\u9650\u5236\u548c\u4f18\u5148\u9009\u62e9\u90fd\u53d1\u9001\u7ed9\u7f51\u7ad9\u670d\u52a1\u5668\u3002 \u5982\u679c\u7f51\u7ad9\u670d\u52a1\u5668\u6709\u4ee5\u591a\u79cd\u8bed\u8a00\u8868\u793a\u7684\u8d44\u6e90\u7248\u672c\uff0c\u5b83\u5c31\u80fd\u628a\u5185\u5bb9\u7528\u6211\u4eec\u6700\u4f18\u9009\u7684\u8bed\u8a00\u8868\u793a\u51fa\u6765\u3002</p> <pre class=hljs><code class="language-undefined">\u670d\u52a1\u5668\u4e5f\u53ef\u4ee5\u6839\u636e <span class=hljs-keyword>Accept</span>-Language \u9996\u90e8\u751f\u6210\u9002\u5408\u7528\u6237\u8bed\u8a00\u7684\u52a8\u6001\u5185\u5bb9\uff0c\u6216\u636e\u6b64\u9009\u62e9\u56fe\u50cf\uff0c\u6216\u9009\u62e9\u9002\u5408\u76ee\u6807\u8bed\u8a00\u7684\u5546\u4e1a\u4fc3\u9500\u7b49\u3002</code></pre><h4 id=\u8bed\u8a00\u6807\u8bb0\u7684\u7c7b\u578b>\u8bed\u8a00\u6807\u8bb0\u7684\u7c7b\u578b</h4> <p>\u5728 RFC3066\uff0c\u201cTags for the Identification of Languages\u201d(\u6807\u8bc6\u8bed\u8a00\u7684\u6807\u8bb0)\u4e2d\u8bb0\u5f55\u4e86\u8bed\u8a00\u6807\u8bb0\u7684\u6807\u51c6\u5316\u8bed\u6cd5\u3002</p> <p>\u53ef\u4ee5\u7528\u8bed\u8a00\u6807\u8bb0\u6765\u8868\u793a:</p> <ul> <li>\u4e00\u822c\u7684\u8bed\u8a00\u5206\u7c7b(\u6bd4\u5982es\u4ee3\u8868\u897f\u73ed\u7259\u8bed);</li> <li>\u7279\u5b9a\u56fd\u5bb6\u7684\u8bed\u8a00(\u6bd4\u5982 en-GB \u4ee3\u8868\u82f1\u56fd\u82f1\u8bed);</li> <li>\u8bed\u8a00\u7684\u65b9\u8a00(\u6bd4\u5982no-bok\u6307\u632a\u5a01\u7684\u4e66\u9762\u8bed);</li> <li>\u5730\u533a\u6027\u7684\u8bed\u8a00(\u6bd4\u5982 sgn-US-MA \u4ee3\u8868\u7f8e\u56fd\u9a6c\u6492\u8461\u8404\u56ed\u5c9b\u4e0a\u7684\u624b\u8bed);</li> <li>\u6807\u51c6\u5316\u7684\u975e\u53d8\u79cd\u8bed\u8a00(\u6bd4\u5982i-navajo);</li> <li>\u975e\u6807\u51c6\u7684\u8bed\u8a00(\u6bd4\u5982 x-snowboarder-slang)\u3002</li> </ul> <h4 id=\u5b50\u6807\u8bb0>\u5b50\u6807\u8bb0</h4> <p>\u8bed\u8a00\u6807\u8bb0\u6709\u4e00\u4e2a\u6216\u591a\u4e2a\u90e8\u5206\uff0c\u7528\u8fde\u5b57\u53f7\u5206\u9694\uff0c\u79f0\u4e3a\u5b50\u6807\u8bb0:</p> <ul> <li>\u7b2c\u4e00\u4e2a\u5b50\u6807\u8bb0\u79f0\u4e3a\u4e3b\u5b50\u6807\u8bb0\uff0c\u5176\u503c\u662f\u6807\u51c6\u5316\u7684;</li> <li>\u7b2c\u4e8c\u4e2a\u5b50\u6807\u8bb0\u662f\u53ef\u9009\u7684\uff0c\u9075\u5faa\u5b83\u81ea\u5df1\u7684\u547d\u540d\u6807\u51c6; </li> <li>\u5176\u4ed6\u5c3e\u968f\u7684\u5b50\u6807\u8bb0\u90fd\u662f\u672a\u6ce8\u518c\u7684\u3002</li> </ul> <p>\u4e3b\u5b50\u6807\u8bb0\u4e2d\u53ea\u80fd\u542b\u6709\u5b57\u6bcd(A ~ Z)\u3002 \u5176\u540e\u7684\u5b50\u6807\u8bb0\u53ef\u4ee5\u542b\u6709\u5b57\u6bcd\u548c\u6570\u5b57\uff0c\u957f\u5ea6\u6700\u591a 8 \u4e2a\u5b57\u7b26\u3002</p> <p>Example:</p> <pre class=hljs><code class=language-text>\u9a6c\u6492\u8461\u8404\u56ed\u5c9b\u4e0a\u7684\u624b\u8bed\n\nsgn-US-MA\n\nsgn -&gt; \u7b2c\u4e00\u4e2a\u5b50\u6807\u8bb0 \uff08\u624b\u8bed\uff09\nUS -&gt; \u7b2c\u4e8c\u4e2a\u5b50\u6807\u8bb0 \uff08\u7f8e\u56fd\uff09\nMA -&gt; \u7b2c\u4e09\u4e2a\u5b50\u6807\u8bb0 \uff08\u9a6c\u8428\u8bf8\u585e\u5dde\u7684\u5730\u533a\u6027\u53d8\u4f53\uff09\n</code></pre><h4 id=\u5927\u5c0f\u5199>\u5927\u5c0f\u5199</h4> <p>\u6240\u6709\u7684\u6807\u8bb0\u90fd\u662f\u4e0d\u533a\u5206\u5927\u5c0f\u5199\u7684\uff0c\u4e5f\u5c31\u662f\u8bf4\uff0c\u6807\u8bb0 en \u548c eN \u662f\u7b49\u4ef7\u7684\u3002 \u4f46\u662f\uff0c\u4e60\u60ef\u4e0a\u7528\u5168\u5c0f\u5199\u6765\u8868\u793a\u4e00\u822c\u7684\u8bed\u8a00\uff0c\u800c\u7528\u5168\u5927\u5199\u6765\u8868\u793a\u7279\u5b9a\u7684\u56fd\u5bb6\u3002</p> <h4 id=iana\u8bed\u8a00\u6807\u8bb0\u6ce8\u518c>IANA\u8bed\u8a00\u6807\u8bb0\u6ce8\u518c</h4> <p>\u7b2c\u4e00\u4e2a\u548c\u7b2c\u4e8c\u4e2a\u8bed\u8a00\u5b50\u6807\u8bb0\u7684\u503c\u7531\u5404\u79cd\u6807\u51c6\u6587\u6863\u4ee5\u53ca\u76f8\u5173\u7684\u7ef4\u62a4\u7ec4\u7ec7\u5b9a\u4e49\u3002 IANA20 \u4f9d\u636e RFC 3066 \u4e2d\u6982\u62ec\u7684\u89c4\u5219\u6765\u7ba1\u7406\u6807\u51c6\u7684\u8bed\u8a00\u6807\u8bb0\u5217\u8868\u3002</p> <p>\u5982\u679c\u8bed\u8a00\u6807\u8bb0\u7531\u6807\u51c6\u7684\u56fd\u5bb6\u548c\u8bed\u8a00\u503c\u7ec4\u6210\uff0c\u6807\u8bb0\u5c31\u4e0d\u9700\u8981\u4e13\u95e8\u6ce8\u518c\u3002 \u53ea\u6709\u90a3\u4e9b\u65e0\u6cd5 \u7528\u6807\u51c6\u7684\u56fd\u5bb6\u548c\u8bed\u8a00\u503c\u6784\u6210\u7684\u8bed\u8a00\u6807\u8bb0\u624d\u9700\u8981\u4e13\u95e8\u5411 IANA \u6ce8\u518c 21\u3002</p> <h4 id=\u7b2c\u4e00\u4e2a\u5b50\u6807\u8bb0--\u540d\u5b57\u7a7a\u95f4>\u7b2c\u4e00\u4e2a\u5b50\u6807\u8bb0--\u540d\u5b57\u7a7a\u95f4</h4> <p>\u7b2c\u4e00\u4e2a\u5b50\u6807\u8bb0\u901a\u5e38\u662f\u6807\u51c6\u5316\u7684\u8bed\u8a00\u8bb0\u53f7\uff0c\u9009\u81ea ISO 639 \u4e2d\u7684\u8bed\u8a00\u6807\u51c6\u96c6\u5408\u3002 \u4e0d\u8fc7\u4e5f\u53ef\u4ee5\u7528\u5b57\u6bcd i \u6765\u6807\u8bc6\u5728 IANA \u4e2d\u6ce8\u518c\u7684\u540d\u5b57\uff0c\u6216\u7528 x \u8868\u793a\u79c1\u6709\u7684\u6216\u8005\u6269\u5c55\u7684\u540d\u5b57\u3002</p> <ul> <li>2 \u4e2a\u5b57\u7b26\uff0c\u90a3\u5c31\u662f\u6765\u81ea ISO 63922 \u548c 639-1 \u6807\u51c6\u7684\u8bed\u8a00\u4ee3\u7801;</li> <li>3 \u4e2a\u5b57\u7b26\uff0c\u90a3\u5c31\u662f\u6765\u81ea ISO 639-2 \u6807\u51c6\u53ca\u5176\u6269\u5c55\u7684\u8bed\u8a00\u4ee3\u7801;</li> <li>\u5b57\u6bcd i\uff0c\u8be5\u8bed\u8a00\u6807\u8bb0\u662f\u5728 IANA \u663e\u5f0f\u6ce8\u518c\u7684;</li> <li>\u5b57\u6bcd x\uff0c\u8be5\u8bed\u8a00\u6807\u8bb0\u662f\u79c1\u6709\u7684\u3001\u975e\u6807\u51c6\u7684\uff0c\u6216\u6269\u5c55\u7684\u5b50\u6807\u8bb0\u3002</li> </ul> <h4 id=\u7b2c\u4e8c\u4e2a\u5b50\u6807\u8bb0--\u540d\u5b57\u7a7a\u95f4>\u7b2c\u4e8c\u4e2a\u5b50\u6807\u8bb0--\u540d\u5b57\u7a7a\u95f4</h4> <p>\u7b2c\u4e8c\u4e2a\u5b50\u6807\u8bb0\u901a\u5e38\u662f\u6807\u51c6\u5316\u7684\u56fd\u5bb6\u8bb0\u53f7\uff0c\u9009\u81ea ISO 3166 \u4e2d\u7684\u56fd\u5bb6\u4ee3\u7801\u548c\u5730\u533a\u6807\u51c6\u96c6\u5408\u3002 \u4e0d\u8fc7\u4e5f\u53ef\u4ee5\u662f\u5728 IANA \u6ce8\u518c\u8fc7\u7684\u5176\u4ed6\u5b57\u7b26\u4e32\u3002</p> <ul> <li>2 \u4e2a\u5b57\u7b26\uff0c\u90a3\u5c31\u662f ISO 3166 \u4e2d\u5b9a\u4e49\u7684\u56fd\u5bb6 / \u5730\u533a;</li> <li>3~8 \u4e2a\u5b57\u7b26\uff0c\u53ef\u80fd\u662f\u5728 IANA \u4e2d\u6ce8\u518c\u7684\u503c;</li> <li>\u5355\u4e2a\u5b57\u7b26\uff0c\u8fd9\u662f\u975e\u6cd5\u7684\u60c5\u51b5\u3002</li> </ul> <h4 id=\u5176\u4f59\u5b50\u6807\u8bb0--\u540d\u5b57\u7a7a\u95f4>\u5176\u4f59\u5b50\u6807\u8bb0--\u540d\u5b57\u7a7a\u95f4</h4> <p>\u9664\u4e86\u6700\u957f\u53ef\u4ee5\u5230 8 \u4e2a\u5b57\u7b26(\u5b57\u6bcd\u548c\u6570\u5b57)\u4e4b\u5916\uff0c\u7b2c\u4e09\u4e2a\u548c\u5176\u540e\u7684\u5b50\u6807\u8bb0\u6ca1\u6709\u7279\u6b8a\u89c4\u5219\u3002</p> <h3 id=\u56fd\u9645\u5316\u7684uri>\u56fd\u9645\u5316\u7684URI</h3> <p>\u76f4\u5230\u4eca\u5929\uff0cURI \u8fd8\u6ca1\u6709\u4e3a\u56fd\u9645\u5316\u63d0\u4f9b\u8db3\u591f\u7684\u652f\u6301\u3002</p> <h4 id=\u5168\u7403\u6027\u7684\u53ef\u8f6c\u6284\u80fd\u529b\u4e0e\u6709\u610f\u4e49\u7684\u5b57\u7b26\u7684\u8f83\u91cf>\u5168\u7403\u6027\u7684\u53ef\u8f6c\u6284\u80fd\u529b\u4e0e\u6709\u610f\u4e49\u7684\u5b57\u7b26\u7684\u8f83\u91cf</h4> <p>URI \u7684\u8bbe\u8ba1\u8005\u4eec\u5e0c\u671b\u4e16\u754c\u4e0a\u6bcf\u4e2a\u4eba\u90fd\u80fd\u901a\u8fc7\u7535\u5b50\u90ae\u4ef6\u3001\u7535\u8bdd\u3001\u516c\u544a\u677f\uff0c\u751a\u81f3\u65e0\u7ebf\u7535\u6765\u5171\u4eab URI\u3002 \u4ed6\u4eec\u8fd8\u5e0c\u671b URI \u5bb9\u6613\u4f7f\u7528\u548c\u8bb0\u5fc6\uff0c\u4f46\u8fd9\u4e24\u4e2a\u76ee\u6807\u662f\u76f8\u4e92\u51b2\u7a81\u7684\u3002</p> <p>URI \u7684\u8bbe\u8ba1\u8005\u4eec\u89c9\u5f97\u786e\u4fdd\u8d44\u6e90\u6807\u8bc6\u7b26\u7684\u53ef\u8f6c\u6284\u80fd\u529b(transcribability)\u548c\u5171\u4eab\u80fd\u529b\u6bd4\u8ba9\u5b83\u4eec\u7531\u6700\u6709\u610f\u4e49\u7684\u5b57\u7b26\u7ec4\u6210\u66f4\u52a0\u91cd\u8981\uff0c \u56e0\u6b64(\u5982\u4eca\u7684)URI \u57fa\u672c\u4e0a\u662f\u7531 ASCII \u5b57\u7b26\u7684\u53d7\u9650\u5b50\u96c6\u6784\u6210\u7684\u3002</p> <h4 id=uri\u5b57\u7b26\u96c6\u5408>URI\u5b57\u7b26\u96c6\u5408</h4> <p>URI \u4e2d\u5141\u8bb8\u51fa\u73b0\u7684 US-ASCII \u5b57\u7b26\u7684\u5b50\u96c6\uff0c\u53ef\u4ee5\u88ab\u5206\u6210\u4fdd\u7559\u3001\u672a\u4fdd\u7559\u4ee5\u53ca\u8f6c\u4e49\u5b57\u7b26 \u8fd9\u51e0\u7c7b\u3002</p> <p>\u672a\u4fdd\u7559\u7684\u5b57\u7b26\u53ef\u7528\u4e8e URI \u5141\u8bb8\u5176\u51fa\u73b0\u7684\u4efb\u4f55\u90e8\u5206\u3002 \u4fdd\u7559\u7684\u5b57\u7b26\u5728\u5f88\u591a URI \u4e2d\u90fd\u6709\u7279\u6b8a\u7684\u542b\u4e49\uff0c\u56e0\u6b64\u4e00\u822c\u6765\u8bf4\u4e0d\u80fd\u4f7f\u7528\u5b83\u4eec\u3002</p> <table> <thead> <tr> <th>\u5b57\u7b26\u7c7b\u522b</th> <th>\u5b57\u7b26\u5217\u8868</th> </tr> </thead> <tbody><tr> <td>\u672a\u4fdd\u7559</td> <td>[A-Za-z0-9] - _ . ! ~ * &#39; ( )</td> </tr> <tr> <td>\u4fdd\u7559</td> <td>; / ? : @ &amp; = &quot; $ ,</td> </tr> <tr> <td>\u8f6c\u4e49</td> <td>%<hex><hex></hex></hex></td> </tr> </tbody></table> <h4 id=\u8f6c\u4e49\u56fd\u9645\u5316\u5b57\u7b26>\u8f6c\u4e49\u56fd\u9645\u5316\u5b57\u7b26</h4> <p>\u9700\u8981\u6ce8\u610f\u7684\u662f\uff0c\u8981\u8f6c\u4e49\u7684\u503c\u672c\u8eab\u5e94\u8be5\u5728 US-ASCII \u4ee3\u7801\u503c\u7684\u8303\u56f4\u5185(0 ~ 127)\u3002</p> <h4 id=uri\u4e2d\u7684\u6a21\u6001\u5207\u6362>URI\u4e2d\u7684\u6a21\u6001\u5207\u6362</h4> <p>\u6709\u4e9b URI \u4e5f\u7528 ASCII \u5b57\u7b26\u7684\u5e8f\u5217\u6765\u8868\u793a\u5176\u4ed6\u5b57\u7b26\u96c6\u4e2d\u7684\u5b57\u7b26\u3002 \u8fd9\u5728\u4e00\u4e9b\u672c\u5730\u5316\u7684\u73af\u5883\u4e2d\u53ef\u4ee5\u5de5\u4f5c\uff0c\u4f46\u8fd9\u79cd\u65b9\u5f0f\u6ca1\u6709\u8fdb\u884c\u826f\u597d\u7684\u5b9a\u4e49\uff0c \u800c\u4e14\u6ca1\u6709\u6807\u51c6\u5316\u7684\u65b9\u6848\u6765\u8bc6\u522b URL \u6240\u4f7f\u7528\u7684\u7279\u5b9a\u7f16\u7801\u3002</p> <h3 id=\u5176\u4ed6\u9700\u8981\u8003\u8651\u7684\u5730\u65b9>\u5176\u4ed6\u9700\u8981\u8003\u8651\u7684\u5730\u65b9</h3> <h4 id=\u9996\u90e8\u548c\u4e0d\u5408\u89c4\u8303\u7684\u6570\u636e>\u9996\u90e8\u548c\u4e0d\u5408\u89c4\u8303\u7684\u6570\u636e</h4> <p>HTTP \u9996\u90e8\u5fc5\u987b\u7531 US-ASCII \u5b57\u7b26\u96c6\u4e2d\u7684\u5b57\u7b26\u6784\u6210\u3002 \u4e0d\u8fc7\uff0c\u5e76\u4e0d\u662f\u6240\u6709\u7684\u5ba2\u6237\u7aef\u548c\u670d\u52a1\u5668\u90fd\u6b63\u786e\u5730\u5b9e\u73b0\u4e86\u8fd9\u4e00\u70b9\uff0c\u4f60\u53ef\u80fd\u4f1a\u65f6\u4e0d\u65f6\u6536\u5230\u4e00\u4e9b\u4ee3\u7801\u503c\u5927\u4e8e 127 \u7684\u975e\u6cd5\u5b57\u7b26\u3002</p> <h4 id=\u65e5\u671f>\u65e5\u671f</h4> <p>HTTP \u7684\u89c4\u8303\u4e2d\u660e\u786e\u5b9a\u4e49\u4e86\u5408\u6cd5\u7684 GMT \u65e5\u671f\u683c\u5f0f\uff0c\u4f46\u8981\u77e5\u9053\u5e76\u975e\u6240\u6709 Web \u670d\u52a1\u5668\u548c\u5ba2\u6237\u7aef\u90fd\u9075\u5b88\u8fd9\u4e9b\u89c4\u5219\u3002</p> <p>\u4f8b\u5982\uff0c\u6211\u4eec\u66fe\u89c1\u8fc7 Web \u670d\u52a1\u5668\u53d1\u9001\u7684\u65e0\u6548 HTTP Date (\u65e5\u671f)\u9996\u90e8\u4e2d\u7684\u6708\u4efd\u662f\u7528\u672c\u5730\u8bed\u8a00\u8868\u793a\u7684\u3002</p> <p>HTTP \u5e94\u7528\u7a0b\u5e8f\u5e94\u5f53\u5c1d\u8bd5\u5bb9\u5fcd\u4e00\u4e9b\u4e0d\u5408\u89c4\u77e9\u7684\u65e5\u671f\uff0c\u4e0d\u80fd\u5728\u63a5\u6536\u7684\u65f6\u5019\u5d29\u6e83\u3002</p> <h4 id=\u57df\u540d>\u57df\u540d</h4> <p>DNS \u76ee\u524d\u8fd8\u4e0d\u652f\u6301\u5728\u57df\u540d\u4e2d\u4f7f\u7528\u56fd\u9645\u5316\u7684\u5b57\u7b26\u3002 \u73b0\u5728\u6b63\u5728\u8fdb\u884c\u652f\u6301\u591a\u8bed\u8a00\u7684\u57df\u540d\u7684\u76f8\u5173\u6807\u51c6\u5316\u5de5\u4f5c\uff0c\u4f46\u8fd8\u6ca1\u6709\u88ab\u5e7f\u6cdb\u90e8\u7f72\u3002</p> <p>\u5b9e\u9645\u56fd\u9645\u5316\u57df\u540d\u5e94\u8be5\u662f\u4f60\u7528IDN\u6765\u7f16\u7801\uff0c\u4f46\u662f\u5728\u672c\u5730mDNS\u65f6\uff0c\u53ef\u80fd\u4f1a\u53d1\u9001\u975eascii\u5b57\u7b26\u3002</p> <h3 id=\u66f4\u591a\u4fe1\u606f>\u66f4\u591a\u4fe1\u606f</h3> <h4 id=\u4e92\u8054\u7f51\u7684\u56fd\u9645\u5316>\u4e92\u8054\u7f51\u7684\u56fd\u9645\u5316</h4> <ul> <li><a href=http://www.w3.org/International/ >http://www.w3.org/International/</a></li> </ul> <p>\u201cMaking the WWW Truly World Wide\u201d(\u201c\u4f7f WWW \u771f\u6b63\u904d\u5e03\u5168\u7403\u201d)\u2014\u2014W3C \u56fd\u9645\u5316\u548c\u672c\u5730\u5316\u7f51\u7ad9\u3002</p> <ul> <li><a href=http://www.ietf.org/rfc/rfc2396.txt>http://www.ietf.org/rfc/rfc2396.txt</a></li> </ul> <p>RFC 2396\uff0c\u201cUniform Resource Identifiers(URI):Generic Syntax\u201d(\u201c\u7edf\u4e00\u8d44\u6e90 \u63cf\u8ff0\u7b26:\u4e00\u822c\u8bed\u6cd5\u201d)\uff0c\u662f URI \u7684\u5b9a\u4e49\u6587\u6863\u3002\u8be5\u6587\u6863\u4e2d\u5305\u62ec\u4e86\u63cf\u8ff0\u56fd\u9645\u5316 URI \u4e2d \u7684\u5b57\u7b26\u96c6\u9650\u5236\u65b9\u9762\u7684\u7ae0\u8282\u3002</p> <ul> <li>CJKV Information Processing(\u300a\u4e2d\u65e5\u97e9\u8d8a\u4fe1\u606f\u5904\u7406\u300b)</li> </ul> <p>Ken Lunde \u8457\uff0c\u7531 O&#39;Reilly &amp; Associates \u516c\u53f8\u51fa\u7248\u3002CJKV \u662f\u4e9a\u6d32\u8bed\u8a00\u7684\u7535\u5b50\u5316 \u5b57\u7b26\u5904\u7406\u65b9\u9762\u7684\u6743\u5a01\u7ecf\u5178\u3002\u4e9a\u6d32\u8bed\u8a00\u7684\u5b57\u7b26\u96c6\u591a\u79cd\u591a\u6837\u3001\u590d\u6742\u96be\u61c2\uff0c\u4f46\u8fd9\u672c\u4e66\u8be6 \u7ec6\u4ecb\u7ecd\u4e86\u5927\u578b\u5b57\u7b26\u96c6\u7684\u5404\u79cd\u6807\u51c6\u6280\u672f\u3002</p> <ul> <li><a href=http://www.ietf.org/rfc/rfc2277.txt>http://www.ietf.org/rfc/rfc2277.txt</a></li> </ul> <p>RFC 2277\uff0c\u201cIETF Policy on Character Sets and Languages\u201d(\u201cIETF \u5173\u4e8e\u5b57\u7b26\u96c6 \u548c\u8bed\u8a00\u7684\u7b56\u7565\u201d)\uff0c \u5176\u4e2d\u8bb0\u5f55\u4e86\u4e92\u8054\u7f51\u5de5\u7a0b\u6307\u5bfc\u7ec4(Internet Engineering Steering Group\uff0cIESG)\u5e94\u7528\u7684\u5f53\u524d\u7b56\u7565\uff0c \u8fd9\u4e9b\u7b56\u7565\u662f\u4e3a\u4e86\u914d\u5408\u4e92\u8054\u7f51\u5de5\u7a0b\u4efb\u52a1\u7ec4 (Internet Engineering Task Force\uff0cIETF)\u5e2e\u52a9\u5404\u79cd\u4e92\u8054\u7f51\u534f\u8bae\u4f7f\u7528\u591a\u79cd\u8bed\u8a00\u548c\u5b57\u7b26\u4ea4\u6362\u6570\u636e\u800c\u505a\u7684\u6807\u51c6\u5316\u52aa\u529b\u3002</p> <h4 id=\u56fd\u9645\u6807\u51c6>\u56fd\u9645\u6807\u51c6</h4> <ul> <li><a href=http://www.iana.org/numbers.htm>http://www.iana.org/numbers.htm</a></li> </ul> <p>IANA \u4e2d\u6709\u5df2\u6ce8\u518c\u7684\u5404\u79cd\u540d\u5b57\u548c\u6570\u5b57\u7f16\u53f7\u7684\u5e93\u3002 \u5176\u4e2d\u7684 Protocol Numbers and Assignments Directory(\u534f\u8bae\u7f16\u53f7\u548c\u5206\u914d\u76ee\u5f55)\u4e2d\u5305\u542b\u4e86\u56e0\u7279\u7f51\u4e0a\u4f7f\u7528\u7684\u5df2\u6ce8\u518c \u5b57\u7b26\u96c6\u8bb0\u5f55\u3002 \u56e0\u4e3a\u6709\u5173\u56fd\u9645\u95f4\u901a\u4fe1\u7684\u5927\u90e8\u5206\u5de5\u4f5c\u90fd\u662f\u5728 ISO \u7684\u9886\u57df\u5185\uff0c\u800c\u4e0d\u662f\u56e0 \u7279\u7f51\u7684\u56e2\u4f53\u5b8c\u6210\u7684\uff0c\u6240\u4ee5 IANA \u7684\u8fd9\u4efd\u5217\u8868\u7b97\u4e0d\u4e0a\u8be6\u5c3d\u3002</p> <ul> <li><a href=http://www.ietf.org/rfc/rfc3066.txt>http://www.ietf.org/rfc/rfc3066.txt</a></li> </ul> <p>RFC 3066\uff0c\u201cTags for the Identification of Languages\u201d(\u201c\u6807\u8bc6\u8bed\u8a00\u7684\u6807\u8bb0\u201d)\uff0c\u63cf \u8ff0\u4e86\u8bed\u8a00\u6807\u8bb0\u3001\u5b83\u4eec\u7684\u503c\uff0c\u4ee5\u53ca\u5982\u4f55\u6784\u9020\u8fd9\u4e9b\u6807\u8bb0\u3002</p> <ul> <li>\u201cCodes for the representation of names of languages\u201d(\u201c\u8868\u793a\u8bed\u8a00\u540d\u79f0\u7684\u4ee3\u7801\u201d) </li> </ul> <p>ISO 639:1988 (E/F)\uff0c\u56fd\u9645\u6807\u51c6\u5316\u7ec4\u7ec7\uff0c\u7b2c 1 \u7248\u3002</p> <ul> <li>\u201cCodes for the representation of names of languages\u2014Part 2: Alpha-3 code\u201d(\u201c\u8868 \u793a\u8bed\u8a00\u540d\u79f0\u7684\u4ee3\u7801\uff0c\u7b2c 2 \u90e8\u5206:Alpha-3 \u4ee3\u7801\u201d)</li> </ul> <p>ISO 639-2:1998\uff0cISO TC46/SC4 \u548c TC37/SC2 \u8054\u5408\u5de5\u4f5c\u7ec4\uff0c\u7b2c 1 \u7248\u3002</p> <ul> <li>\u201cCodes for the representation of names of countries\u201d(\u201c\u8868\u793a\u56fd\u5bb6\u540d\u79f0\u7684\u4ee3\u7801\u201d) </li> </ul> <p>ISO 3166:1988 (E/F)\uff0c\u56fd\u9645\u6807\u51c6\u5316\u7ec4\u7ec7\uff0c\u7b2c 3 \u7248\u3002</p> ';t.exports=a}}]);